import React from 'react';
import './App.css';
import Keypad from './components/Keypad';

class App extends React.Component {
  constructor(props) {
    super(props);
    //this.handleClick = this.handleClick.bind(this);
    this.calculate = this.calculate.bind(this);
    this.reset = this.reset.bind(this);
    this.backspace = this.backspace.bind(this);
    this.state = {
      result: []
    }
  }

  handleClick = (button) => {
    if (button.target.value === "=") {
      this.calculate();
    } else if (button.target.value === "C") {
      this.reset();
    } else if (button.target.value === "CE") {
      this.backspace();
    } else {
      this.setState({
        result: this.state.result + button.target.value
      })
    }
  }

  calculate = () => {
    let result = this.state.result
    if (result) {
      result = eval(result)
      result = String(result)
      this.setState({
        result: [result]
      })
    }
  }

  reset = () => {
    this.setState({
      result: []
    })
  }

  backspace = () => {
    this.setState({
      result: this.state.result.toString().substring(0, this.state.result.length - 1)
    })
  }

  render() {
    return (
      <div className="container">
        <div className="jumbotron jumbotron-fluid">
          <h1>Simple Calculator</h1>
        </div>
        <Keypad
        displayButton={this.handleClick}
        result={this.state.result}
        />
      </div>
    )
  }
}

export default App;
